<?php/*第一个参数的格式分别表示: a - "am" 或是 "pm"  A - "AM" 或是 "PM"  d - 几日，二位数字，若不足二位则前面补零; 如: "01" 至 "31"  D - 星期几，三个英文字母; 如: "Fri"  F - 月份，英文全名; 如: "January"  h - 12 小时制的小时; 如: "01" 至 "12"  H - 24 小时制的小时; 如: "00" 至 "23"  g - 12 小时制的小时，不足二位不补零; 如: "1" 至 12"  G - 24 小时制的小时，不足二位不补零; 如: "0" 至 "23"  i - 分钟; 如: "00" 至 "59"  j - 几日，二位数字，若不足二位不补零; 如: "1" 至 "31"  l - 星期几，英文全名; 如: "Friday"  m - 月份，二位数字，若不足二位则在前面补零; 如: "01" 至 "12"  n - 月份，二位数字，若不足二位则不补零; 如: "1" 至 "12"  M - 月份，三个英文字母; 如: "Jan"  s - 秒; 如: "00" 至 "59"  S - 字尾加英文序数，二个英文字母; 如: "th"，"nd"  t - 指定月份的天数; 如: "28" 至 "31"  U - 总秒数  w - 数字型的星期几，如: "0" (星期日) 至 "6" (星期六)  Y - 年，四位数字; 如: "1999"  y - 年，二位数字; 如: "99"  z - 一年中的第几天; 如: "0" 至 "365"  */$hot=checkhot();return $this->respText($hot); function checkhot(){	$t=date("YnjGs");	$data=date("Ymd");	$url='http://m.mtime.cn/Service/callback.mi?Ajax_CallBack=true&Ajax_CallBackType=Mtime.Mobile.Pages.CallbackService&Ajax_CallBackMethod=RemoteCallbackSameDomain&Ajax_RequestUrl=http%3A%2F%2Fm.mtime.cn%2F%23!%2F&t='.$t.'&Ajax_CallBackArgument0=%2FShowtime%2FLocationMovies.api&Ajax_CallBackArgument1=locationId%3D365';	$movie = file_get_contents($url);	$movie= json_decode($movie,1);	$hot_date=$movie['date']; 	$n=0;	$str="广州热映电影\n$hot_date\n\n";	for($i=0;$i<8;$i++){					$lx='';			$version='';			$id=$movie['ms'][$i]['id'];//ID			$tcn=$movie['ms'][$i]['tCn'];//中文名			$ten=$movie['ms'][$i]['tEn'];//英文名			$r=$movie['ms'][$i]['r'];//评分			$img=$movie['ms'][$i]['img'];//海报			$zhuyan=$movie['ms'][$i]['dN'].','.$movie['ms'][$i]['aN1'].','.$movie['ms'][$i]['aN2'];//主演						foreach($movie['ms'][$i]['p'] as $b){				$lx.=$b." ";//类型			}			$p=$movie['ms'][$i]['p']['0'];			$rd=$movie['ms'][$i]['rd'];//上映时间			$pc=$movie['ms'][$i]['d'];//片长			$commonSpecial=$movie['ms'][$i]['commonSpecial'];//特别描述			foreach($movie['ms'][$i]['versions'] as $b){				$version.=$b['version']." ";//version			}									$str.=$tcn."\n".$ten."\n类型：".$lx."\n主演：".$zhuyan."\n上映时间：".$rd."\n片长：".$pc."\n版本：".$version."\n特别描述：".$commonSpecial."\n评分：".$r."\n\n";	} 		return $str;}?>